<?php/*///////////////////////////////////////////////////////////////////////Part of the code from the book Building Findable Websites: Web Standards, SEO, and Beyondby Aarron Walter (aarron@buildingfindablewebsites.com)http://buildingfindablewebsites.comDistrbuted under Creative Commons licensehttp://creativecommons.org/licenses/by-sa/3.0/us////////////////////////////////////////////////////////////////////////*/function storeAddress(){		// Validation	if(!$_GET['email']){ return "מלא/י את כתובת המייל שלך"; } 	if(!preg_match("/^[_a-z0-9-]+(\.[_a-z0-9-]+)*@[a-z0-9-]+(\.[a-z0-9-]+)*$/i", $_GET['email'])) {		return "כתובת המייל לא חוקית"; 	}	require_once('MCAPI.class.php');	// grab an API Key from http://admin.mailchimp.com/account/api/	$api = new MCAPI('ecae8dcaa663e6cc42c7c26373dd96fa-us19');		// grab your List's Unique Id by going to http://admin.mailchimp.com/lists/
	// Click the "settings" link for the list - the Unique Id is at the bottom of that page. 	$list_id = "e7094a192b";	if($api->listSubscribe($list_id, $_GET['email'], '') === true) {		// It worked!			return 'היכנס/י למייל כדי לאשר את ההרשמה';	}else{		// An error ocurred, return error message			return 'Error: ' . $api->errorMessage;	}	}// If being called via ajax, autorun the functionif($_GET['ajax']){ echo storeAddress(); }?>
